import 'package:flutter/material.dart';void main() {  runApp(const ClipRRectEXM());}class ClipRRectEXM extends StatelessWidget {  const ClipRRectEXM({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        appBar: AppBar(          title: Text('مثال ClipRRect'),        ),        body: SingleChildScrollView(            child: Column(          children: [            Center(              child: ClipRRect(                borderRadius: BorderRadius.circular(20), // شعاع گوشه‌ها                clipBehavior: Clip.antiAlias, // رفتار برش                child: Image.asset(                  'Assets/image1.jpg',                  width: 300,                  height: 300,                  fit: BoxFit.cover,                ),              ),            ),            SizedBox(              height: 20,            ),            ClipRRect(              borderRadius: BorderRadius.only(                topLeft: Radius.circular(50),                topRight: Radius.circular(0),                bottomLeft: Radius.circular(0),                bottomRight: Radius.circular(50),              ),              child: Container(                child: DecoratedBox(                  decoration: BoxDecoration(                    boxShadow: [                      BoxShadow(                        color: Colors.black12.withOpacity(0.5),                        offset: Offset(-20, 20),                        blurRadius: 50,                      )                    ],                    image: DecorationImage(                        image: AssetImage('Assets/image3.jpg'),                        fit: BoxFit.cover),                  ),                ),                width: 300,                height: 300,              ),            ),            SizedBox(              height: 20,            ),            ClipRRect(              borderRadius: BorderRadius.circular(20),              clipBehavior: Clip.hardEdge,              child: Image.asset(                'Assets/image2.jpg',                width: 300,                height: 300,                fit: BoxFit.cover,              ),            ),            SizedBox(              height: 20,            ),            ClipRRect(              borderRadius: BorderRadius.circular(30),              clipBehavior: Clip.antiAliasWithSaveLayer,              child: Container(                width: 300,                height: 300,                color: Colors.pink,                child: Center(                  child: Text(                    'Hello!',                    style: TextStyle(color: Colors.white, fontSize: 24),                  ),                ),              ),            )          ],        )),      ),    );  }}