import 'package:flutter/material.dart';void main() {  runApp(const OpacityEXM());}class OpacityEXM extends StatelessWidget {  const OpacityEXM({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        appBar: AppBar(          title: Text('OpacityEXM'),        ),        body: SingleChildScrollView(            child: Center(                child: Column(          children: [            Opacity(              opacity: 1.0,              alwaysIncludeSemantics: true,              child: Container(                  decoration: BoxDecoration(                    image: DecorationImage(                      image: AssetImage('Assets/image1.jpg'),                      fit: BoxFit.cover,                    ),                  ),                  width: 300,                  height: 300,                  child: Text('کاملاً واضح')),            ),            Opacity(              opacity: 0.5,              child: Container(                  decoration: BoxDecoration(                    image: DecorationImage(                      image: AssetImage('Assets/image2.jpg'),                      fit: BoxFit.cover,                    ),                  ),                  width: 300,                  height: 300,                  child: Text('شفافیت 50٪')),            ),            Opacity(              opacity: 0.1,              alwaysIncludeSemantics: true,              child: Container(                  decoration: BoxDecoration(                    image: DecorationImage(                      image: AssetImage('Assets/image3.jpg'),                      fit: BoxFit.cover,                    ),                  ),                  width: 300,                  height: 300,                  child: Text('کاملاً شفاف (نامرئی)')),            ),          ],        ))),      ),    );  }}