import 'package:flutter/material.dart';void main() {  runApp(const SizedBoxEXM());}class SizedBoxEXM extends StatelessWidget {  const SizedBoxEXM({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        appBar: AppBar(          title: Text('SizedBoxEXM'),        ),        body: Center(          child: Column(            children: [              SizedBox(                width: 100, // Width of the box is 100 pixels                child: Text('Fixed Width'),              ),              SizedBox(                height: 50, // Height of the box is 50 pixels                child: Text('Fixed Height'),              ),              SizedBox(                width: 100,                height: 50,                child: Container(color: Colors.blue),              ),              Column(                children: [                  Text('Above Spacer'),                  SizedBox(height: 20), // Adds vertical space                  Text('Below Spacer'),                ],              ),              SizedBox(                width: 150,                height: 100,                child: Container(                  color: Colors.teal,                  child: Center(child: Text('Fixed Size')),                ),              ),              SizedBox(height: 50), // Adds 50 pixels of vertical empty space              SizedBox(                width: 200,                height: 100,                child: ElevatedButton(                  onPressed: () {},                  child: Text('Button with Fixed Size'),                ),              ),            ],          ),        ),      ),    );  }}