import 'package:flutter/material.dart';void main() {  runApp(const TextEXM());}class TextEXM extends StatelessWidget {  const TextEXM({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        appBar: AppBar(          title: Text('TextEXM'),        ),        body: Column(          children: [            Text('Hello, world!'),            Text(              textAlign: TextAlign.right,              'Style text',              style: TextStyle(                fontSize: 30,                decoration: TextDecoration.underline,                fontWeight: FontWeight.bold,              ),            ),            Text(              overflow: TextOverflow.fade,              maxLines: 1,              'Sajad Nowroozi Sajad Nowroozi This is a very long text that will overflow.',            ),            Text(              overflow: TextOverflow.fade,              softWrap: true,              'Sajad Nowroozi Sajad Nowroozi This is a very long text that will overflow.',            ),            Text(              overflow: TextOverflow.fade,              softWrap: true,              textDirection: TextDirection.rtl,              'Sajad Nowroozi Sajad Nowroozi This is a very long text that will overflow.',            ),            Text(              textScaleFactor: 1.5,              'Sajad Nowroozi Sajad Nowroozi This is a very long text that will overflow.',            ),            Text(              'سجاد نوروزی',              locale: Locale('fa', 'IR'),            ),            Text(              'Labelled Text',              semanticsLabel: 'This text is labeled for accessibility.',            ),            Text(              'Text with StrutStyle',              strutStyle: StrutStyle(                fontSize: 16,                height: 1.5,              ),            ),            Text(              'Customized Text Example',              style: TextStyle(                fontSize: 24,                color: Colors.teal,                fontWeight: FontWeight.w600,                letterSpacing: 2.5,                wordSpacing: 6.0,                decoration: TextDecoration.underline,                decorationColor: Colors.tealAccent,                decorationStyle: TextDecorationStyle.dashed,              ),              textAlign: TextAlign.center,              overflow: TextOverflow.ellipsis,              maxLines: 2,              textScaleFactor: 1.2,            )          ],        ),      ),    );  }}